<script setup lang="ts">
import { useI18n } from "vue-i18n";
import { gameStyleList } from '@/api/userGame'
import { ref } from "vue";
import { gameInfoStore } from '@/stores/gameStore';
const gameStore = gameInfoStore();

const { t } = useI18n();
const $t = key => {
  return t("common-header." + key);
};
const activeNav = ref<string>("Home");
const activeClass = activeType => {
  return gameStore.tabSelect === activeType ? "nav-item--active" : "";
};

const setActiveNav = (tab) => {
  activeNav.value = tab
  gameStore.setTab(tab)
}

// const toList = (group)=>{
//   router.push({ name: 'game-list', params: { group } });
// }

const gameTypes = ref([])
const getGameLists = async () => {
  const res = await gameStyleList({})
  if (res.success) {
    gameTypes.value = res.data.list
  }
}
getGameLists()
</script>

<style lang="less" scoped>
.common-header {
  &__nav {
    display: flex;
    overflow: auto;
    padding: 10px;

    .nav-item {
      height: 45px;
      padding: 0 10px;
      display: flex;
      align-items: center;
      border-radius: 45px;

      >svg {
        flex-shrink: 0;
        height: 18px;
        width: 18px;
        margin-right: 6px;
      }

      >span {
        font-size: 14px;
      }

      &--active {
        border-radius: 45px;
        background: var(--fog-21);
      }
    }
  }
}
</style>
